const variableNames = [
    "AcFixed",
    "BalanceInterval",
    "BalanceTime",
    "BattSoc",
    "BmsBalancing",
    "ChargeEnabled",
    "ChargeToFull",
    "CurrentLimitOverride",
    "LastBalance",
    "MaxSoc",
    "MultiRestartSoc",
    "MultiState",
    "ShoreMaxCurrent",
    "SolarMgmt",
    "SystemVoltage",
    "VesselInput",
];

const globalValues = Object.fromEntries(
    variableNames.map(name => [name, global.get(name)])
);

return {
    payload: globalValues,
};

const currentTimestamp = Date.now();
const LastBalance = flow.get("LastBalance");
const BalanceInterval = flow.get("BalanceInterval");
let ChargeToFull = global.get("ChargeToFull");

if (ChargeToFull === 1) {
    node.status({fill: "yellow", shape: "dot", text: "Charge to Full"});
    return;
} else if (ChargeToFull === 0) {
    const millisSinceLastBalance = currentTimestamp - LastBalance;
    const millisBalanceInterval = BalanceInterval * 24 * 60 * 60 * 1000;
    if (millisSinceLastBalance >= millisBalanceInterval) {
        node.status({fill: "yellow", shape: "dot", text: "Charge to Full"});
        return {
            payload: 1,
        };
    }
}

node.status({fill: "green", shape: "dot", text: "Normal Operation"});

const timestamp = msg.payload;
const date = new Date(timestamp);

// Extracting day, month, and year from the date
const day = String(date.getDate()).padStart(2, '0');
const month = String(date.getMonth() + 1).padStart(2, '0'); // Adding 1 because months are zero-based
const year = date.getFullYear();

// Constructing the formatted date string
const formattedDate = `${day}-${month}-${year}`;

return {
    payload: formattedDate,
};

const AbsorptionThreshold = flow.get("AbsorptionThreshold");

const cvl = msg.payload;

if (cvl > AbsorptionThreshold) {
    node.status({fill: "green", shape: "dot", text: "Requesting Absorption"});
    return {payload: 1};
}

node.status({fill: "red", shape: "dot", text: "Not Requesting Absorption"});
return {payload: 0};

const AbsorptionThreshold = flow.get("AbsorptionThreshold");

const voltage = msg.payload;

const balancing = voltage >= AbsorptionThreshold;

if (balancing) {
    node.status({fill: "green", shape: "dot", text: "Balancing"});
} else {
    node.status({fill: "green", shape: "ring", text: "Idle"});
}

return {
    payload: + balancing,  // bool -> int
};

const LastBalance = flow.get("LastBalance");

return {
    payload: {
        LastBalance,
    },
}

const balancing = msg.payload;
const now = msg.timestamp;

if (balancing == 0) {
    node.status({fill: "grey", shape: "ring", text: "Idle"});
    return;
}

const BalanceStartTime = flow.get("BalanceStartTime");
const BalanceTime = flow.get("BalanceTime");
const BalanceTimeMillis = BalanceTime * 60 * 1000;

if (now > BalanceStartTime + BalanceTimeMillis) {
    node.status({fill: "green", shape: "dot", text: "Done!"});
    return {
        payload: 0,
    }
} else {
    node.status({fill: "yellow", shape: "dot", text: "Balancing..."});
}

const secondsAgo = msg.payload;

const now = new Date();
const last_full = new Date(now.getTime() - secondsAgo * 1000);

// Extracting day, month, and year from the date
const day = String(last_full.getDate()).padStart(2, '0');
const month = String(last_full.getMonth() + 1).padStart(2, '0'); // Adding 1 because months are zero-based
const year = last_full.getFullYear();

const formatted_last_full = `${day}-${month}-${year}`;

return {
    payload: formatted_last_full,
};

const BMSRequestAbsorption = flow.get("BMSRequestAbsorption");

// Let the batteries continue to balance as long as the BMS wants to
if (msg.payload >= 99.5 && BMSRequestAbsorption === 0) {
    return msg;
}

const system_voltage = msg.payload;
const threshold = 1.18125 * system_voltage;  // 14.175 / 28.35V

node.status({text: threshold});

return {
    payload: threshold,
}

//- switch mode current limit: CurrentLimitOverride
//- Shore Max set Current : ShoreMaxCurrent
//- Generator Max Current : GenMaxCurrent
//- Vessel input : VesselInput
//- AC counter fixed consumers: AcFixed
//- Switch selector shore/gen: ShoreGenSwitch
//- Dynamic fields selection of shore/gen based on switch state: ActualLimit
//- Remaining amps for charger to use: AllowedChargeAmps

// Retrieve values from global context
var CurrentLimitOverride = global.get("CurrentLimitOverride");
var ShoreMaxCurrent = global.get("ShoreMaxCurrent");
var GenMaxCurrent = global.get("GenMaxCurrent");
var VesselInput = global.get("VesselInput");
var AcFixed = global.get("AcFixed");
var ShoreGenSwitch = global.get("ShoreGenSwitch");
var ActualLimit; 

// Check whether "CurrentLimitOverride" is on auto (1) or manual (0), if manual, do nothing; if auto, run the sequence
if (CurrentLimitOverride === 1) {
    if (ShoreGenSwitch === 11) {
        // If ShoreGenSwitch is 1, set ActualLimit equal to ShoreMaxCurrent
        ActualLimit = ShoreMaxCurrent;

        // Debugging: Log that "ShoreMaxCurrent" is selected as the source for ActualLimit
     //   node.warn("Selected source for ActualLimit: ShoreMaxCurrent");
    } else if (ShoreGenSwitch === 10) {
        // If ShoreGenSwitch is 0, set ActualLimit equal to GenMaxCurrent
        ActualLimit = GenMaxCurrent;

        // Debugging: Log that "GenMaxCurrent" is selected as the source for ActualLimit
       //  node.warn("Selected source for ActualLimit: GenMaxCurrent");
    }

    // Calculate the remaining difference, subtract "AcFixed" from "ActualLimit," round to the nearest inferior number, and store the result in global context as "AllowedChargeAmps"
    var AllowedChargeAmps = Math.floor(ActualLimit - AcFixed);
   // node.warn("AllowedChargeAmps: " + AllowedChargeAmps);

    msg.payload = AllowedChargeAmps;
} else if (CurrentLimitOverride === 0) {
    // If "CurrentLimitOverride" is in manual mode (0), do nothing and pass the message as is
    // node.warn("System is in manual mode");
    msg.payload = null;
}

// Retrieve the variables from the global context
var MultiSOCmgmt = global.get("MultiSOCmgmt");
var MultiChargeEnabled = global.get("MultiChargeEnabled");
var MaxSoc = global.get("MaxSoc");
var BattSoc = global.get("BattSoc");
var MultiState = global.get("MultiState");
var BalanceNeed = global.get("BalanceNeed");
var ShoreGenSwitch = global.get("ShoreGenSwitch");
var MultiRestartSoc = global.get("MultiRestartSoc");
var CycleMgmt = global.get("CycleMgmt");


// Create an object to hold the variables
var variables = {
    MultiSOCmgmt: MultiSOCmgmt,
    MultiChargeEnabled: MultiChargeEnabled,
    MaxSoc: MaxSoc,
    BattSoc: BattSoc,
    MultiState: MultiState,
    BalanceNeed: BalanceNeed,
    ShoreGenSwitch: ShoreGenSwitch,
    CycleMgmt: CycleMgmt,
    MultiRestartSoc: MultiRestartSoc
};

// Output the variables as a payload
msg.payload = variables;

return msg;

// Retrieve the value from the input node's payload
var MultiChargeEnabled = msg.payload;

// Set the value as a global variable
global.set("MultiChargeEnabled", MultiChargeEnabled);

// Pass the message to the next node (if needed)
return msg;

// retrieve the state of CycleMgmt from global storage 
// retrieve the values from StorRestartSoc and SailRestartSoc from global storage
// If CycleMgmt is 1 store null as MultiRestartSoc, if CycleMgmt is 2 store StorRestartSoc as MultiRestartSoc, if CycleMgmt is 3 store SailRestartSoc as MultiRestartSoc



// retrieve the state of CycleMgmt from global storage
var CycleMgmt = global.get("CycleMgmt");

// retrieve the values from StorRestartSoc and SailRestartSoc from global storage
var StorRestartSoc = global.get("StorRestartSoc");
var SailRestartSoc = global.get("SailRestartSoc");

// Declare MultiRestartSoc variable
var MultiRestartSoc;

// Debug each retrieved value
// node.warn("CycleMgmt: " + CycleMgmt);
// node.warn("StorRestartSoc: " + StorRestartSoc);
// node.warn("SailRestartSoc: " + SailRestartSoc);
// If CycleMgmt is 1, store null as MultiRestartSoc
if (CycleMgmt === 1) {
    MultiRestartSoc = null;
//    node.warn("Value to be stored in MultiRestartSoc: null");
} 
// If CycleMgmt is 2, store StorRestartSoc as MultiRestartSoc
else if (CycleMgmt === 2) {
    MultiRestartSoc = StorRestartSoc;
//    node.warn("Value to be stored in MultiRestartSoc: " + StorRestartSoc);
} 
// If CycleMgmt is 3, store SailRestartSoc as MultiRestartSoc
else if (CycleMgmt === 3) {
    MultiRestartSoc = SailRestartSoc;
 //   node.warn("Value to be stored in MultiRestartSoc: " + SailRestartSoc);
}

// Store MultiRestartSoc in the global context
global.set("MultiRestartSoc", MultiRestartSoc);
// node.warn("Value to be stored in MultiRestartSoc: " + MultiRestartSoc);
// Pass the message to the next node
return msg;

// retrieve the state of CycleMgmt from global storage 
// retrieve the values from StorMaxSoc and SailMaxSoc from global storage
// If CycleMgmt is 1 store null as MaxSoc, if CycleMgmt is 2 store StorMaxSoc as MaxSoc, if CycleMgmt is 3 store SailMaxSoc as MaxSoc


// Retrieve the state of CycleMgmt from global storage
var CycleMgmt = global.get("CycleMgmt");

// Retrieve the values from StorMaxSoc and SailMaxSoc from global storage
var StorMaxSoc = global.get("StorMaxSoc");
var SailMaxSoc = global.get("SailMaxSoc");

// Debug each retrieved value
// node.warn("CycleMgmt: " + CycleMgmt);
// node.warn("StorMaxSoc: " + StorMaxSoc);
// node.warn("SailMaxSoc: " + SailMaxSoc);

var MaxSoc = null; // Default value as null

// If CycleMgmt is 1, store null as MaxSoc
if (CycleMgmt === 1) {
    MaxSoc = null;
 //   node.warn("Value to be stored in MaxSoc: null");
} 
// If CycleMgmt is 2, store StorMaxSoc as MaxSoc
else if (CycleMgmt === 2) {
    MaxSoc = StorMaxSoc;
//    node.warn("Value to be stored in MaxSoc: " + StorMaxSoc);
} 
// If CycleMgmt is 3, store SailMaxSoc as MaxSoc
else if (CycleMgmt === 3) {
    MaxSoc = SailMaxSoc;
//    node.warn("Value to be stored in MaxSoc: " + SailMaxSoc);
}

// Store MaxSoc in the global context
global.set("MaxSoc", MaxSoc);
// node.warn("Value to be stored in MaxSoc: " + MaxSoc);
// Pass the message to the next node 
return msg;

// Retrieve the value from the input node's payload
var ChargeEnabledSol = msg.payload;

// Set the value as a global variable
global.set("ChargeEnabledSol", ChargeEnabledSol);

// Pass the message to the next node (if needed)
return msg;

// Retrieve the value from the input node's payload
var AltChargeEnabled = msg.payload;

// Set the value as a global variable
global.set("AltChargeEnabled", AltChargeEnabled);

// Pass the message to the next node (if needed)
return msg;

const CycleMgmt = global.get("CycleMgmt");
const StorMaxSoc = global.get("StorMaxSoc");
const SailMaxSoc = global.get("SailMaxSoc");

const CycleMgmtModes = Object.freeze({
    OFF: 1,
    STORAGE: 2,
    SAILING: 3,
});

let MaxSoc = null;

if (CycleMgmt === CycleMgmtModes.OFF) {
    MaxSoc = null;
} else if (CycleMgmt === CycleMgmtModes.STORAGE) {
    MaxSoc = StorMaxSoc;
} else if (CycleMgmt === CycleMgmtModes.SAILING) {
    MaxSoc =  SailMaxSoc;
}

node.status({text: MaxSoc});
return {
    payload: MaxSoc,
}

const CycleMgmt = global.get("CycleMgmt");
const StorRestartSoc = global.get("StorRestartSoc");
const SailRestartSoc = global.get("SailRestartSoc");

const CycleMgmtModes = Object.freeze({
    OFF: 1,
    STORAGE: 2,
    SAILING: 3,
});

let MultiRestartSoc = null;

if (CycleMgmt === CycleMgmtModes.OFF) {
    MultiRestartSoc = null;
} else if (CycleMgmt === CycleMgmtModes.STORAGE) {
    MultiRestartSoc = StorRestartSoc;
} else if (CycleMgmt === CycleMgmtModes.SAILING) {
    MultiRestartSoc =  SailRestartSoc;
}

node.status({text: MultiRestartSoc});
return {
    payload: MultiRestartSoc,
}

const ChargeToFull = global.get("ChargeToFull");
const MultiState = global.get("MultiState");
const BattSoc = global.get("BattSoc");
const MaxSoc = global.get("MaxSoc");
const MultiRestartSoc = global.get("MultiRestartSoc");
let MultiChargeEnabled = global.get("MultiChargeEnabled");

const MultiModes = Object.freeze({
    CHARGER_ONLY: 1,
    INVERTER_ONLY: 2,
    ON: 3,
    OFF: 4,
});

if (MultiChargeEnabled === 0) {
    if (ChargeToFull === 1) {
        MultiChargeEnabled = 1;
    }
    if (BattSoc <= MultiRestartSoc) {
        MultiChargeEnabled = 1;
    }
} else if (MultiChargeEnabled === 1) {
    if (BattSoc >= MaxSoc) {
        if (ChargeToFull === 1) {
            node.status({fill: "yellow", shape: "dot", text: "Balancing"});
            return;
        }
        MultiChargeEnabled = 0;
    }
} else if (MultiChargeEnabled === undefined) {
    node.warn("Startup: Guessing MultiChargeEnabled from MultiState: " + MultiState);
    if (MultiState === MultiModes.INVERTER_ONLY
        || MultiState === MultiModes.OFF) {
        MultiChargeEnabled = 0;
    } else if (MultiState === MultiModes.CHARGER_ONLY
               || MultiState == MultiModes.ON) {
        MultiChargeEnabled = 1;
    }
}

if (MultiChargeEnabled === 1) {
    node.status({fill: "green", shape: "dot", text: "Enable"});
} else {
    node.status({fill: "red", shape: "dot", text: "Disable"});
}

return {
    payload: MultiChargeEnabled,
};

const MultiSOCmgmt = global.get("MultiSOCmgmt");
let MultiChargeEnabled = global.get("MultiChargeEnabled");
const MultiState = global.get("MultiState");

const MultiModes = Object.freeze({
    CHARGER_ONLY: 1,
    INVERTER_ONLY: 2,
    ON: 3,
    OFF: 4,
});

if (MultiSOCmgmt !== 1) {
    node.status({fill: "red", shape: "ring", text: "Management Disabled"});
    return;
}

if (MultiChargeEnabled === 0) {
    if (MultiState === MultiModes.CHARGER_ONLY) {
        node.status({fill: "red", shape: "dot", text: "Off"});
        return {
            payload: MultiModes.OFF,
        };
    } else if (MultiState === MultiModes.ON) {
        node.status({fill: "red", shape: "dot", text: "Charging off"});
        return {
            payload: MultiModes.INVERTER_ONLY,
        };
    }
} else if (MultiChargeEnabled === 1) {
    if (MultiState === MultiModes.OFF) {
        node.status({fill: "green", shape: "dot", text: "On"});
        return {
            payload: MultiModes.CHARGER_ONLY,
        };
    } else if (MultiState === MultiModes.INVERTER_ONLY) {
        node.status({fill: "green", shape: "dot", text: "Charging on"});
        return {
            payload: MultiModes.ON,
        };
    }
}

const BattSoc = global.get("BattSoc");
const ChargeToFull = global.get("ChargeToFull");
const SailMaxSoc = global.get("SailMaxSoc");
const SolarMgmt = flow.get("SolarMgmt");

const SolarMgmtStates = Object.freeze({
    ON: 1,
    OFF: 4,
    AUTO: 20,
});

if (SolarMgmt === SolarMgmtStates.OFF) {
    node.status({fill: "red", shape: "ring", text: "Manual Disable"});
    return {
        payload: 0,
    }
}
if (BattSoc > SailMaxSoc) {
    if (ChargeToFull === 1) {
        node.status({fill: "yellow", shape: "dot", text: "Charging to Full"});
        return {
            payload: 1,
        };
    }
    node.status({fill: "red", shape: "dot", text: "Disable"});
    return {
        payload: 0,
    };
}

node.status({fill: "green", shape: "dot", text: "Enable"});
return {
    payload: 1,
};

const SolarMgmt = flow.get("SolarMgmt");
const ChargeEnabledSol = flow.get("ChargeEnabledSol");

const SolarMgmtStates = Object.freeze({
    ON: 1,
    OFF: 4,
    AUTO: 20,
});

let SolarState;

if (SolarMgmt === SolarMgmtStates.OFF || SolarMgmt === SolarMgmtStates.ON) {
    SolarState = SolarMgmt;
} else if (SolarMgmt === SolarMgmtStates.AUTO) {
    if (ChargeEnabledSol === 0) {
        SolarState = SolarMgmtStates.OFF;
    } else if (ChargeEnabledSol === 1) {
        SolarState = SolarMgmtStates.ON;
    }
}

if (SolarState === SolarMgmtStates.ON) {
    node.status({fill: "green", shape: "dot", text: "On"});
} else if (SolarState === SolarMgmtStates.OFF) {
    node.status({fill: "red", shape: "dot", text: "Off"});
}

return {
    payload: SolarState,
};

// Retrieve ChargeEnabled from the global context
var AltChargeEnabled = flow.get("AltChargeEnabled");

// Output 0 or 1 based on the value of AltChargeEnabled
if (AltChargeEnabled === 1) {
    msg.payload = 1; // Output 1 if AltChargeEnabled is 1
} else {
    msg.payload = 0; // Output 0 for any other value of ChargeEnabled
}

return msg;

// Retrieve values from global context
var AltChargeEnabled = flow.get("AltChargeEnabled");
var MultiRestartSoc = global.get("MultiRestartSoc");
var BattSoc = global.get("BattSoc");

// Check if ChargeEnabled is 0
if (AltChargeEnabled === 0) {
    // Compare BattSoc to RestartSOC
    if (BattSoc <= MultiRestartSoc) {
        // Set ChargeEnabled to 1
        global.set("AltChargeEnabled", 1);
    }
}

// node.warn("ChargeEnabled: " + ChargeEnabled);
// node.warn("RestartSOC: " + RestartSOC);
// node.warn("BattSoc: " + BattSoc);

// Pass the message to the next node (if needed)
return msg;

// Alternator Management Function
// Retrieve the values from the global context
const AltSOCmgmt = flow.get("AltSOCmgmt");
const PortAltTemp = flow.get("PortAltTemp");
const StbdAltTemp = flow.get("StbdAltTemp");
const AltMaxTemp = flow.get("AltMaxTemp");
const BattSoc = global.get("BattSoc");
const MaxSoc = global.get("MaxSoc");
const MultiChargeEnabled = global.get("MultiChargeEnabled");
let AlternatorState = flow.get("AlternatorState");
let AltChargeEnabled = global.get("AltChargeEnabled");


let tempAlternatorState = AlternatorState;

// Add conditions based on temperatures before running the function:
// If PortAltTemp or StbdAltTemp are bigger than AltMaxTemp, stop function output 0. If PortAltTemp and StbdAltTemp are smaller than AltMaxTemp, continue function
if (PortAltTemp > AltMaxTemp || StbdAltTemp > AltMaxTemp) {
    AlternatorState = 0;
} else if (PortAltTemp < AltMaxTemp && StbdAltTemp < AltMaxTemp) {
    if (AltSOCmgmt === 0) {
        AlternatorState = 0;
    } else if (AltSOCmgmt === 1) {
        if (AltChargeEnabled === 0) {
            AlternatorState = 0;
        } else if (AltChargeEnabled === 1) {
            if (MultiChargeEnabled === 0) {
                if (BattSoc < MaxSoc) {
                    AlternatorState = 1;
                } else {
                    AlternatorState = 0;

                    AltChargeEnabled = 0;
                    global.set("AltChargeEnabled", AltChargeEnabled);
                }
            } else if (MultiChargeEnabled === 1) {
                AlternatorState = 1;
            }
        }
    }
}

msg.payload = AlternatorState;

return msg;

// Retrieve values from global context
var ChargeEnabledAlt = global.get("ChargeEnabledAlt");
var RestartSOC = global.get("RestartSOC");
var BattSoc = global.get("BattSoc");

// Check if ChargeEnabledAlt  is 0
if (ChargeEnabledAlt === 0) {
    // Compare BattSoc to RestartSOC
    if (BattSoc <= RestartSOC) {
        // Set ChargeEnabledAlt  to 1
        global.set("ChargeEnabledAlt", 1);
    }
}

// node.warn("ChargeEnabledAlt : " + ChargeEnabledAlt );
// node.warn("RestartSOC: " + RestartSOC);
// node.warn("BattSoc: " + BattSoc);

// Pass the message to the next node (if needed)
return msg;
